const port_no = 8122;
var express = require('express');
var mongodb = require('mongodb');

var connection;

var allowCrossDomain = function (req, res, next) {
    res.header('Access-Control-Allow-Origin', '*');
    res.header('Access-Control-Allow-Methods','GET,PUT,POST,DELETE');
    res.header('Access-Control-Allow-Headers','Content-Type');
    next();
};


var app = express();
app.use(express.bodyParser());
app.use(allowCrossDomain);
app.use('/scripts', express.static(__dirname + '/scripts'));
app.use('/css', express.static(__dirname + '/css'));
app.use(express.static(__dirname));



app.listen(port_no, function () {


var uname = 'a_roy';
var password = 'A00443079';
var dbname = 'a_roy';


var host = '127.0.0.1';
var port = '27017';
connection = 'mongodb://' + uname + ':' + password + '@' +
    host + ':' + port + '/' + dbname;

        console.log('Listening on port %d',
                port_no);
});


app.post('/newUni', function (request, response) {    
mongodb.connect(connection, function (error, db) { 
    if (error) {
        throw error;
    }    
    collection = db.collection("unidb");
collection.insert(request.body, 
        function (err, result) {
           if (err) {
               return response.send(400,'An error occurred saving a record.');
           }
        db.close();
      });
    });
   return response.send(200, "Record saved successfully.");
});


app.post('/removeUniversity', function (request, response) {

mongodb.connect(connection, function (error, db) {
    if (error) {
        throw error;
    }
    collection = db.collection("unidb");

collection.remove({'Name':request.body.Name}, 
        function (err, result) {
           if (err) {
               return response.send(400,'An error occurred while deleting a record.');
           }
        db.close();
       return response.send(200, result);
      });
   });
});


app.post('/searchUniversity', function (request, response) {

mongodb.connect(connection, function (error, db) {
    if (error) {
        throw error;
    }
    collection = db.collection("unidb");

collection.find({'Name':request.body.Name},
        function (err, result) {
           if (err) {
               return response.send(400,'An error occurred.');
           }
        result.toArray(
            function (err, resultArray) {
            if (err) {
                return response.send(400, 'An error occurred while processing your query.');
            }
          return response.send(200, resultArray);
        db.close();
    });
  });
  });
});

app.post('/allUniversities', function (request, response) {

mongodb.connect(connection, function (error, db) {
    if (error) {
        throw error;
    }
    collection = db.collection("unidb");

collection.find(
        function (err, result) {
           if (err) {
               return response.send(400,'An error occurred.');
           }
        result.toArray(
            function (err, resultArray) {
            if (err) {
                return response.send(400, 'An error occurred while processing your request.');
            }
          return response.send(200, resultArray);
        db.close();
    });
  });
});
});